    /**
     * Test checks time that is required to parse large html
     *
     * @dataProvider dataProvider
     * @group 61818
     * @return void
     */
    public function testTime($html, $time)
    {
        $timer = microtime(true);
        SugarCleaner::cleanHtml($html);
        $timer = microtime(true) - $timer;
        $this->assertLessThanOrEqual($time, $timer);
    }
